mapper.select:
	select  count(*)
	from t_wash_order o 
	where o.org_id = #{orgId,jdbcType=INTEGER}
		
			and o.state in
  			  
					#{item}
			
		
		
			and o.order_time  >=#{startTime,jdbcType=TIMESTAMP}
		
		
			and o.order_time  <=#{endTime,jdbcType=TIMESTAMP}

select o.id as id, o.state as state, o.state_note as stateNote, o.order_no as orderNo, u.name as customerName, u.mobile as customerMobile, r.name as regionName, o.user_note as userNote, a.pn as autoPN, o.order_time as orderTime, o.bill_time as billTime, (select f.name from t_user f where f.id = o.washer_id) as washerName, o.accept_time as acceptTime, o.begin_time as beginTime, o.finish_time as finishTime, ct.name as couponName, (select group_concat(wtp.name) from t_wash_type wtp, t_wash_order_item wi where wi.order_id = o.id and wtp.id = wi.wash_type_id) as washTypeName, (select sum(wi.coupon_amount) from t_wash_order_item wi where wi.order_id = o.id) as couponAmount, (select sum(wi.fixed_amount) from t_wash_order_item wi where wi.order_id = o.id) as fixedAmount, (select sum(wi.final_amount) from t_wash_order_item wi where wi.order_id = o.id) as finalAmount, o.grade_washer as washerGrade, o.grade_user as customerGrade, p.pay_type as PayType from t_wash_order o left join t_user u on o.user_id = u.id and u.user_type = 8 left join t_auto a on o.auto_id = a.id left join t_org org on o.org_id = org.id left join t_region r on o.region_id = r.id left join t_wash_order_item w on o.id = w.order_id left join t_wash_type wt on w.wash_type_id = wt.id left join t_coupon c on w.coupon_id = c.id left join t_coupon_type ct on c.coupon_type_id = ct.id left join t_pay p on p.order_id = o.id where 1=1
and o.id = #{id,jdbcType=INTEGER}
group by o.order_no;

select o.id as id, o.state as state, o.state_note as stateNote, o.order_no as orderNo, o.user_id as userId, u.name as customerName, u.mobile as customerMobile, r.name as regionName, o.user_note as userNote, a.pn as autoPN, o.order_time as orderTime, o.bill_time as billTime, (select f.name from t_user f where f.id = o.washer_id) as washerName, o.accept_time as acceptTime, o.begin_time as beginTime, o.finish_time as finishTime, ct.name as couponName, (select group_concat(wtp.name) from t_wash_type wtp, t_wash_order_item wi where wi.order_id = o.id and wtp.id = wi.wash_type_id) as washTypeName, (select sum(wi.coupon_amount) from t_wash_order_item wi where wi.order_id = o.id) as couponAmount, (select sum(wi.fixed_amount) from t_wash_order_item wi where wi.order_id = o.id) as fixedAmount, (select sum(wi.final_amount) from t_wash_order_item wi where wi.order_id = o.id) as finalAmount, o.grade_washer as washerGrade, o.grade_user as customerGrade, o.photo_url_1 as photoUrl1, o.photo_url_2 as photoUrl2, o.photo_url_3 as photoUrl3, p.pay_type as payType from t_wash_order o left join t_user u on o.user_id = u.id and u.user_type = 8 left join t_auto a on o.auto_id = a.id left join t_org org on o.org_id = org.id left join t_region r on o.region_id = r.id left join t_wash_order_item w on o.id = w.order_id left join t_wash_type wt on w.wash_type_id = wt.id left join t_coupon c on w.coupon_id = c.id left join t_coupon_type ct on c.coupon_type_id = ct.id left join t_pay p on p.order_id = o.id where 1=1
and o.state = #{state,jdbcType=INTEGER}
and o.state in
#{item}
and o.washer_id = #{washerId,jdbcType=INTEGER}
and o.org_id = #{orgId,jdbcType=INTEGER}
group by o.order_no order by o.bill_time desc;

select o.id as id, o.state as state, o.state_note as stateNote, o.order_no as orderNo, u.name as customerName, u.mobile as customerMobile, r.name as regionName, o.user_note as userNote, a.pn as autoPN, o.order_time as orderTime, o.bill_time as billTime, (select f.name from t_user f where f.id = o.washer_id) as washerName, o.accept_time as acceptTime, o.begin_time as beginTime, o.finish_time as finishTime, ct.name as couponName, (select group_concat(wtp.name) from t_wash_type wtp, t_wash_order_item wi where wi.order_id = o.id and wtp.id = wi.wash_type_id) as washTypeName, (select sum(wi.coupon_amount) from t_wash_order_item wi where wi.order_id = o.id) as couponAmount, (select sum(wi.fixed_amount) from t_wash_order_item wi where wi.order_id = o.id) as fixedAmount, (select sum(wi.final_amount) from t_wash_order_item wi where wi.order_id = o.id) as finalAmount, o.grade_washer as washerGrade, o.grade_user as customerGrade, o.photo_url_1 as photoUrl1, o.photo_url_2 as photoUrl2, o.photo_url_3 as photoUrl3, p.pay_type as payType from t_wash_order o left join t_user u on o.user_id = u.id and u.user_type = 8 left join t_auto a on o.auto_id = a.id left join t_org org on o.org_id = org.id left join t_region r on o.region_id = r.id left join t_wash_order_item w on o.id = w.order_id left join t_wash_type wt on w.wash_type_id = wt.id left join t_coupon c on w.coupon_id = c.id left join t_coupon_type ct on c.coupon_type_id = ct.id left join t_pay p on p.order_id = o.id where 1=1
and o.user_id = #{userId,jdbcType=INTEGER}
group by o.order_no order by o.bill_time desc;

select o.id as id, o.state as state, o.state_note as stateNote, o.order_no as orderNo, u.name as customerName, o.user_note as userNote, a.pn as autoPN, o.order_time as orderTime, o.bill_time as billTime, o.accept_time as acceptTime, o.begin_time as beginTime, o.finish_time as finishTime from t_wash_order o left join t_user u on o.user_id = u.id and u.user_type = 8 left join t_auto a on o.auto_id = a.id left join t_org org on o.org_id = org.id where o.org_id = #{orgId,jdbcType=INTEGER} or org.path like CONCAT(#{orgPath,jdbcType=VARCHAR},'%' )
and o.state in
#{item}
and o.order_time >=#{startTime,jdbcType=TIMESTAMP}
and o.order_time <=#{endTime,jdbcType=TIMESTAMP}
order by o.bill_time desc
limit #{pageStart,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}
